import axios from 'axios';
import React, { useEffect, useState} from 'react';


const baseURL = "http://localhost:3500/posts"

const Task2 = () => {
    const [post, setPost] = useState([])


   useEffect(() => {
     axios.get(baseURL).then((response) => {
         setPost(response.data);
     })

   }, [])
   const handleChange = (ti) => {
    setPost({ title: ti.target.value });


   }




    return (
        <div>

            <div>{post.map((item) => (
           <label>
            <button>
              {item.author}
            </button>
            Title:
              <input type ="text" name ="Title" onChange= {handleChange(item.title)} />





           </label>
          ))}</div>

            <div>{post}</div>

        </div>
    )
}

export default Task2
