import React, { useState } from 'react';

function Task3() {
  const data = [
    { id: 1, name: "home" },
    { id: 2, name: "about" }
  ];

  const handleClick = (id) => {
    console.log("data", data);
    console.log(id);
    if (id === 1) data[0].name = "home2";
    else data[1].name = "about2";
  };

  return (
    <div>
      <div>{data.map((item, index) => (
        <button key={index} name={item.id} onClick={() => handleClick(item.id)}>
          {item.name}
        </button>



      ))}</div>
      <div>{data.name}</div>
    </div>

  );
}
export default Task3;
