import React, { useState } from 'react';

function Task3() {
    const [data, setData] = useState([
        { id: 1, name: "home" },
        { id: 2, name: "about" }
      ]);

      console.log("data", data);
      const handleClick = (id) => {
        console.log(id);
        if (id === 1)
          setData((data) => [
            ...data.filter((item) => item.id !== 1),
            { id: 1, name: "home2" }
          ]);
        if (id === 2)
          setData((data) => [
            ...data.filter((item) => item.id !== 2),
            { id: 2, name: "about2" }
          ]);
      };

      return (
        <div>
          {data.map((item, index) => (
            <button key={index} name={item.id} onClick={() => handleClick(item.id)}>
              {item.name}
            </button>
          ))}
        </div>
      );
}
export default Task3;
