const data = [
    { id: 1, name: "home" },
    { id: 2, name: "about" }
  ]



const Task = () => {


  const handleClick = (datainfo) => {
    return datainfo.id = 1 ? datainfo.name = "house"
           : datainfo.name = "about us"
  }


    return (

        <div>
      {data.map((datainfo) => {
        return (
          <>
            <button onClick={() => handleClick(datainfo)}>{datainfo.name}</button>
            <div>{handleClick}</div>
          </>
        )
      })}
    </div>


    )
}

export default Task
